// https://firebase.google.com/docs/web/setup#available-libraries

// Your web app's Firebase configuration
const firebaseConfig = {
  apiKey: "AIzaSyAbjK6vvbzaVeHXcYGCqYUWSs85QGLnH_4",
  authDomain: "ubd-counseling.firebaseapp.com",
  databaseURL: "https://ubd-counseling-default-rtdb.asia-southeast1.firebasedatabase.app",
  projectId: "ubd-counseling",
  storageBucket: "ubd-counseling.appspot.com",
  messagingSenderId: "334645300318",
  appId: "1:334645300318:web:0e38f8113274ee54abf8cf"
};

// Initialize Firebase
const app = initializeApp(firebaseConfig);

const signIn = async (email, password) => {
  try {
    const userCredential = await signInWithEmailAndPassword(
      auth,
      email,
      password
    );
    const user = userCredential.user;
    return true
  } catch (error) {
    return {error: error.message}
  }
};

const signOut = async() => {
  try {
    await signOut(auth)
    return true
  } catch (error) {
    return false
  }
};

const signUp = async (email, password) => {
  try {
    const userCredential = await createUserWithEmailAndPassword(
      auth,
      email,
      password
    );
    const user = userCredential.user;
    await addDoc(collection(db, "users"), {
      uid: user.uid,
      email: user.email,
    });
    return true
  } catch (error) {
    return {error: error.message}
  }
};
